header.script:
function getFavoritsdata() {

    $.ajax({
        type: "POST",
        url: "/myaccountV2/dashboard/getMyFav",

        success: function (data) {

            if (data) {
                var adids = [];
                var parsedData = JSON.parse(data);
                if (parsedData.ads_count === 0) {
                    $('.favourit-count').css('display', 'none');
                } else {
                    $('.favourit-count').css('display', 'block');
                    $('.favourit-count').html(parsedData.ads_count);

                }
            }
        },
    });
} 

window.addEventListener('load', getFavoritsdata);
